import React from 'react';
import Header from './Header';
import SearchCrypto from './SearchCrypto';
import Result from './result';
import './style.css';

class App extends React.Component {
    state = {coinInfo: null}

    setCoinCodes = async (info) => {
        const prom = await new Promise((res, rej) => {
            res(info);
        }).then((info) => {
            info = {
                coinCode: info.coinCode,
                currCode: info.currencyCode
            };
        })

        this.setState({coinInfo: info});
        console.log(this.state.coinInfo);
    };
    render() {
        return(
            <div className = "view">
                <Header
                    title = "Crypto Tracker"
                    subtitle = "A minimalist cryptocurrency ticker and nothing else."
                />
                <SearchCrypto onSubmit = {this.setCoinCodes}/>
                <Result/>
            </div>
        );
    }
}

export default App;
