import React from 'react';
import Header from './Header';
import SearchCrypto from './SearchCrypto';
import './style.css';

class App extends React.Component {
    state = {coinInfo: null}

    setCoinCodes = (info) => {
        info = {
            coinCode: coinInfo.coinCode,
            currCode: coinInfo.currencyCode
        };

        this.setState({coinInfo: info});
        console.log(this.state.coinInfo);
    };
    render() {
        return(
            <div className = "view">
                <Header
                    title = "Crypto Tracker"
                    subtitle = "A minimalist cryptocurrency ticker and nothing else."
                />
                <SearchCrypto onSubmit = {this.setCoinCodes}/>
            </div>
        );
    }
}

export default App;
