import React, {useState, useEffect} from 'react';
import axios from 'axios';

const Result = (props) => {
    //state = {price: ""};
    const [price, setPrice] = useState("");
    //console.log(props.token);
    const checker = () => {
        if(price === "") {
            return "";
        } else {
            return '$' + parseFloat(price).toFixed(2);
        }
    }
    /*Once the Results component has recieved the crypto info (coin and currency codes),
    we can pass them (as props) into our sendReq() method, which will open a GET request via
    Axios with these codes passed into the request URL.
    */
   
    const sendReq = async () => {

       await axios.get(`https://api.cryptonator.com/api/ticker/${props.token}-${props.currency}`
        ).then(resp => {
            //console.log(resp);
            setPrice(resp.data.ticker.price)
            
        })
    }

    //do this to resolve the memory leak warning from the componentDidUpdate() method
    /*componentDidMount() {
        console.log("Component has mounted.")
    }

    componentDidUpdate() {
        this.sendReq();
    }*/
    useEffect(() => {
        //sendReq();
        return () => {
            sendReq();
        }
    })

    return(
        <div className = "results">
            <h3 className = "coinTitle">{props.token ? props.token.toUpperCase() : ""}</h3>
            <h3 className = "priceTitle">{checker()}</h3>
        </div>
    );
};


export default Result;
