import React, {useState} from 'react';
import Header from './Header';
import SearchCrypto from './SearchCrypto';
import Result from './Result';

import './style.css';

const App = (props) => {
    //const state = {token: "", currency: ""};
    const [coinCodes, setCoinCodes] = useState({token: "doge", currency: "usd"});
    //state = {coinCode: "", currencyCode: ""}

    /*setCoinCodes = (coin, curr) => {
        this.setState({
            coinCode: coin,
            currencyCode: curr
        });
    };*/
    
    return(
        <div className = "view">
            <Header
                title = "Crypto Tracker"
                subtitle = "A minimalist cryptocurrency ticker and nothing else."
            />
            <SearchCrypto onSubmit = {setCoinCodes}/>
            <Result
                token = {coinCodes.token} 
                currency = {coinCodes.currency}
            />
        </div>
    );
};
export default App;
