import React, {useState} from 'react';


const SearchCrypto = (props) => {
    /*state = {
        coinCode: "",
        currencyCode: ""
    }*/
    const [coinInfo, setCoinInfo] = useState({token: "", currency: ""}); 

    const getCoin = (e) => {
        e.preventDefault();
        props.onSubmit(coinInfo.token, coinInfo.currency);
    }

    return(
        <form onSubmit = {getCoin} className = "crypto-form">
            <input
                type = "text" 
                placeholder = "Crypto Code"
                className = "inp coin-code"
                value = {coinCodes.token}
                onChange = {e => {setCoinCodes({token: e.target.value.toUpperCase()})}}
                /*setCoinCodes({token: e.target.value.toUpperCase()})*/
            />
            <input
                type = "text" 
                placeholder = "Fiat Currency Code"
                className = "inp currency-code"
                value = {coinCodes.currency}
                onChange = {e => {setCoinCodes({currency: e.target.value.toUpperCase()})}}
            />
            <button className = "btn" type = "submit">Get Coin</button>
        </form>
    );
}

export default SearchCrypto;
